    /**
     * 参考题解后的改动
     * 思路：把读到过为0的陆地，直接置为1，起到了记忆作用
     * 因为是深度优先，直到结束全局flag的都可以认为是这一组陆地的flag， 可以在主遍历方法里重复使用
     */


    static boolean flag = true;

    public static  int closedIsland2(int[][] grid) {

        height = grid.length;
        width = grid[0].length;

        int result = 0;

        for (int i = 0; i < height; i++) {
            for (int j = 0; j < width; j++) {

                if(grid[i][j]==0){
                    flag = true;
                    DFS(i,j,grid);
                    if(flag){
                        result++;
                    }

                }

            }
        }

        return result;
    }


    public static void DFS(int i, int j, int[][] grid){
//        System.out.println(i+","+j +" in check");

        if(i<0 || i>=height || j<0 || j>=width){
            flag = false;
            return;
        }


        if(grid[i][j]==1){
            return;
        }
        grid[i][j] = 1;
        DFS(i+1,j, grid);
        DFS(i,j+1, grid);
        DFS(i,j-1, grid);
        DFS(i-1,j, grid);

    }
